mapper.select:
SELECT sc.id AS cartId, sc.num, sc.shop_id AS shopId, s.front_name AS shopName, s.logo AS shopLogo, sc.goods_id AS goodsId, sc.sku_id AS skuId, g.name AS goodsName, g.status, IFNULL(gs.price, g.price) AS price, IFNULL(gs.stock, g.stock) AS stock, IFNULL(gs.img, g.main_img) AS goodsImg, gs.specs, g.is_deleted AS mainGoodsDel, IFNULL(gs.is_deleted, g.is_deleted) AS skuGoodsDel FROM shopping_cart sc LEFT JOIN goods g ON sc.goods_id = g.id LEFT JOIN goods_sku gs ON sc.sku_id = gs.id LEFT JOIN shop s ON sc.shop_id = s.id WHERE 1 = 1 AND sc.user_id = #{userId} /*正常商品*/ AND g.status = 1 AND IFNULL(gs.stock, g.stock) > 0 AND g.is_deleted = 0 AND (gs.is_deleted = 0 OR gs.is_deleted IS NULL)
/*已失效商品*/ AND ((g.status = 2 OR IFNULL(gs.stock, g.stock) <= 0) OR g.is_deleted = 1 OR gs.is_deleted = 1)
ORDER BY sc.created_time DESC

mapper.delete:
DELETE FROM shopping_cart
WHERE
    user_id = #{userId}
AND id IN
